package triangle_bottom_left;

public class Alphabet2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(char i ='A'; i <= 'E'; i++) {
			for(char j = 'A'; j <= 'E'; j++) {
				System.out.print(i+" ");
			}
			System.out.println();
		}
		
	}

}
